import {
  Grid,
  Paper,
  Typography,
  Button,
  TextField,
  makeStyles,
  Select,
  MenuItem,
  FormControl,
  InputLabel,
} from '@material-ui/core';

import { Link } from 'react-router-dom';
import axios from 'axios';
import { useState, useEffect } from 'react';

import Navbar from './Navbar';

const useStyles = makeStyles(() => ({
  body: {
    padding: '60px 60px',
    margin: '125px 350px',
  },
  inputBox: {
    width: '300px',
    margin: '-12px',
  },
  submitButton: {
    width: '300px',
    margin: '15px',
    backgroundColor: '#034f84',
    color: 'white',
  },
  LinkColor: {
    textDecoration: 'none',
    color: 'black',
  },
}));

const Signup = (props) => {
  const classes = useStyles();

  const [username, setusername] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [type, setType] = useState('');
  const [error, setError] = useState(false);
  const [loading, setLoading] = useState(false);

  const submitHandler = async (e) => {
    e.preventDefault();
    console.log(username, email, password, type);

    try {
      const config = {
        headers: {
          'Content-type': 'application/json',
        },
      };

      setLoading(true);
      const { data } = await axios.post('localhost:3000/users/login', {
        email,
        password,
      });

      console.log(data);

      localStorage.setItem('userInfo', JSON.stringify(data));
      setLoading(false);
    } catch (e) {
      console.log('Erroe' + e);
      // setError(e.response.data.message);
    }
  };

  return (
    <form onSubmit={submitHandler}>
      <Paper elevation={3} className={classes.body}>
        <Navbar />
        <Grid container direction="column" spacing={4} alignItems="center">
          <Grid item>
            <Typography variant="h4" component="h2">
              Signup
            </Typography>
          </Grid>
          <Grid item>
            <TextField
              id="txtUserName"
              type="text"
              label="Username"
              className={classes.inputBox}
            />
          </Grid>
          <Grid item>
            <TextField
              id="txtEmailId"
              type="email"
              label="Email"
              className={classes.inputBox}
            />
          </Grid>
          <Grid item>
            <TextField
              id="txtPassword"
              type="password"
              label="Password"
              className={classes.inputBox}
            />
          </Grid>
          <Grid item>
            <TextField
              id="txtCPassword"
              type="password"
              label="Confirm Password"
              className={classes.inputBox}
            />
          </Grid>
          <Grid item>
            <FormControl fullWidth className={classes.inputBox}>
              <InputLabel>Type</InputLabel>
              <Select>
                <MenuItem value="Applicant">Applicant</MenuItem>
                <MenuItem value="Recruitmend">Recruitmend</MenuItem>
              </Select>
            </FormControl>
          </Grid>
          <Grid item>
            <Button
              id="btnSignup"
              variant="contained"
              className={classes.submitButton}
              type="submit"
            >
              Signup
            </Button>
          </Grid>
          <Grid item>
            <Typography variant="subtitle2">
              <Link to="/Login" className={classes.LinkColor}>
                Already have an Account?
              </Link>
            </Typography>
          </Grid>
        </Grid>
      </Paper>
    </form>
  );
};

export default Signup;
