import {
  Grid,
  Paper,
  Typography,
  Button,
  TextField,
  makeStyles,
  form,
} from '@material-ui/core';
import { useState } from 'react';
import { Link } from 'react-router-dom';

import Navbar from './Navbar';

// makeStyles use for CSS

const useStyles = makeStyles(() => ({
  body: {
    padding: '60px 60px',
    margin: '125px 350px',
  },
  inputBox: {
    width: '300px',
    margin: '-12px',
  },
  submitButton: {
    width: '300px',
    margin: '15px',
    backgroundColor: '#034f84',
    color: 'white',
  },
  LinkColor: {
    textDecoration: 'none',
    color: 'black',
  },
}));

const Login = (props) => {
  const classes = useStyles();

  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState(false);
  const [loading, setLoading] = useState(false);

  const submitHandler = (e) => {
    e.preventDefault();
    console.log('Hiii');
    console.log(email, password);
  };

  return (
    // Paper use as container , elevation - for shadow

    // <form onSubmit={submitHandler}>
    <Paper elevation={3} className={classes.body} onSubmit={submitHandler}>
      <Navbar />
      <Grid container direction="column" spacing={4} alignItems="center">
        <Grid item>
          {/*  Typography use for size or different heading tag */}

          <Typography variant="h4" component="h2">
            Login
          </Typography>
        </Grid>
        <Grid item>
          <TextField
            id="txtEmailId"
            type="email"
            label="Email"
            className={classes.inputBox}
            onChange={(e) => setEmail(e.target.value)}
            value={email}
          />
        </Grid>
        <Grid item>
          <TextField
            id="txtPassword"
            type="password"
            label="Password"
            className={classes.inputBox}
            onChange={(e) => setPassword(e.target.value)}
            value={password}
          />
        </Grid>
        <Grid item>
          <Button
            id="btnLogin"
            variant="contained"
            className={classes.submitButton}
            onSubmit={submitHandler}
          >
            Login
          </Button>
        </Grid>
        <Grid item>
          <Typography variant="subtitle2">
            <Link to="/Signup" className={classes.LinkColor}>
              Create an Account?
            </Link>
          </Typography>
        </Grid>
      </Grid>
    </Paper>
    // </form>
  );
};

export default Login;
