  //   try {
  //     const config = {
  //       headers: {
  //         'Content-type': 'application/json',
  //       },
  //     };

  //     setLoading(true);
  //     const { data } = await axios.post('localhost:3000/users/login', {
  //       email,
  //       password,
  //     });

  //     console.log(data);

  //     localStorage.setItem('userInfo', JSON.stringify(data));
  //     setLoading(false);
  //   } catch (e) {
  //     console.log('Erroe' + e);
  //     // setError(e.response.data.message);
  //   }
  // };

  // const url = 'http://localhost:5000/users/login';

  // const [user, setUser] = useState({
  //   email: '',
  //   password: '',
  // });

  // const submitHandler = (e) => {
  //   e.preventDefault();
  //   axios
  //     .post(url, {
  //       email: user.email,
  //       password: user.password,
  //     })
  //     .then((res) => {
  //       console.log(res.user);
  //     });
  // };

  // const inputHandler = (e) => {
  //   e.preventDefault();
  //   const newdata = { ...user };
  //   console.log(newdata);
  //   newdata[e.target.id] = e.target.value;
  //   setUser(newdata);
  //   console.log(newdata);
  // };

  const [data, setData] = useState({ email: '', password: '' });
  const [error, setError] = useState('');

  const handleChange = ({ currentTarget: input }) => {
    setData({ ...data, [input.name]: input.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const url = 'http://localhost:8080/api/auth';
      const { data: res } = await axios.post(url, data);
      localStorage.setItem('token', res.data);
      window.location = '/';
    } catch (error) {
      if (
        error.response &&
        error.response.status >= 400 &&
        error.response.status <= 500
      ) {
        setError(error.response.data.message);
      }
    }
  };

  return (
    // Paper use as container , elevation - for shadow

    <form onSubmit={handleSubmit}>
      <Paper elevation={3} className={classes.body}>
        <Navbar />
        <Grid container direction="column" spacing={4} alignItems="center">
          <Grid item>
            {/*  Typography use for size or different heading tag */}

            <Typography variant="h4" component="h2">
              Login
            </Typography>
          </Grid>
          <Grid item>
            <TextField
              id="email"
              type="text"
              label="Email"
              className={classes.inputBox}
              onChange={handleChange}
              value={data.email}
            />
          </Grid>
          <Grid item>
            <TextField
              id="password"
              type="password"
              label="Password"
              className={classes.inputBox}
              onChange={(e) => inputHandler(e)}
              value={user.password}
            />
          </Grid>
          <Grid item>
            <Typography variant="subtitle2">
              <Link to="/resetpassword" className={classes.LinkColor}>
                Forgot Password?
              </Link>
            </Typography>
          </Grid>
          <Grid item>
            <Button
              id="btnLogin"
              variant="contained"
              type="submit"
              className={classes.submitButton}
            >
              Login
            </Button>
          </Grid>
          <Grid item>
            <Typography variant="subtitle2">
              <Link to="/Signup" className={classes.LinkColor}>
                Create an Account?
              </Link>
            </Typography>
          </Grid>
        </Grid>
      </Paper>
    </form>
  );
};

export default Login;
