import {
  Grid,
  Paper,
  Typography,
  Button,
  TextField,
  makeStyles,
} from '@material-ui/core';
import { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
// import cors from 'cors';
import Navbar from './Navbar';

// makeStyles use for CSS

const useStyles = makeStyles(() => ({
  body: {
    padding: '60px 60px',
    margin: '125px 350px',
  },
  inputBox: {
    width: '300px',
    margin: '-12px',
  },
  submitButton: {
    width: '300px',
    margin: '0px 15px',
    backgroundColor: '#034f84',
    color: 'white',
  },
  LinkColor: {
    textDecoration: 'none',
    color: 'black',
  },
}));

const Login = (props) => {
  const classes = useStyles();

  // const [email, setEmail] = useState('');
  // const [password, setPassword] = useState('');
  // const [error, setError] = useState(false);
  // const [loading, setLoading] = useState(false);

  // const submitHandler = async (e) => {
  //   e.preventDefault();
  //   console.log(email, password);

  //   try {
  //     const config = {
  //       headers: {
  //         'Content-type': 'application/json',
  //       },
  //     };

  //     setLoading(true);
  //     const { data } = await axios.post('localhost:3000/users/login', {
  //       email,
  //       password,
  //     });

  //     console.log(data);

  //     localStorage.setItem('userInfo', JSON.stringify(data));
  //     setLoading(false);
  //   } catch (e) {
  //     console.log('Erroe' + e);
  //     // setError(e.response.data.message);
  //   }
  // };

  const url = 'http://localhost:5000/users/login';

  const [user, setUser] = useState({
    email: '',
    password: '',
  });

  const submitHandler = (e) => {
    e.preventDefault();
    axios
      .post(url, {
        username: user.username,
        email: user.email,
        password: user.password,
        cpassword: user.cpassword,
        type: user.type,
      })
      .then((res) => {
        console.log(res.user);
      });
  };

  const inputHandler = (e) => {
    e.preventDefault();
    const newdata = { ...user };
    console.log(newdata);
    newdata[e.target.id] = e.target.value;
    setUser(newdata);
    console.log(newdata);
  };

  return (
    // Paper use as container , elevation - for shadow

    <form onSubmit={(e) => submitHandler(e)}>
      <Paper elevation={3} className={classes.body}>
        <Navbar />
        <Grid container direction="column" spacing={4} alignItems="center">
          <Grid item>
            {/*  Typography use for size or different heading tag */}

            <Typography variant="h4" component="h2">
              Login
            </Typography>
          </Grid>
          <Grid item>
            <TextField
              id="txtEmailId"
              type="text"
              label="Email"
              className={classes.inputBox}
              onChange={(e) => inputHandler(e)}
              value={user.email}
            />
          </Grid>
          <Grid item>
            <TextField
              id="txtPassword"
              type="password"
              label="Password"
              className={classes.inputBox}
              onChange={(e) => inputHandler(e)}
              value={user.password}
            />
          </Grid>
          <Grid item>
            <Typography variant="subtitle2">
              <Link to="/resetpassword" className={classes.LinkColor}>
                Forgot Password?
              </Link>
            </Typography>
          </Grid>
          <Grid item>
            <Button
              id="btnLogin"
              variant="contained"
              type="submit"
              className={classes.submitButton}
            >
              Login
            </Button>
          </Grid>
          <Grid item>
            <Typography variant="subtitle2">
              <Link to="/Signup" className={classes.LinkColor}>
                Create an Account?
              </Link>
            </Typography>
          </Grid>
        </Grid>
      </Paper>
    </form>
  );
};

export default Login;
