import React from 'react';

import {
  Grid,
  Paper,
  Typography,
  Button,
  TextField,
  makeStyles,
  FormControl,
  Select,
  MenuItem,
  InputLabel,
} from '@material-ui/core';

import { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

import Navbar from '../Navbar';

// makeStyles use for CSS

const useStyles = makeStyles(() => ({
  body: {
    padding: '60px 60px',
    margin: '125px 350px',
  },
  inputBox: {
    width: '300px',
    margin: '-12px',
  },
  submitButton: {
    width: '300px',
    margin: '0px 15px',
    backgroundColor: '#034f84',
    color: 'white',
  },
  LinkColor: {
    textDecoration: 'none',
    color: 'black',
  },
}));

const AddJob = () => {
  const classes = useStyles();

  // const handleSubmit = (e) => {
  //   // e.preventDefault();
  //   // console.log(jobTypes);
  //   // console.log(e.target.value);
  //   // setJobType();
  //   const url = 'http://localhost:5000/users/register';
  //   axios
  //     .post(url, {
  //       jobType: jobTypes,
  //       // types: user.types,
  //     })
  //     .then((res) => {
  //       console.log(res.user);
  //     });
  // };

  const [jobDetail, setjobDetail] = useState({ jobType: 'Full Time' });

  // const handleInput = (key, value) => {
  //   setjobDetail({
  //     ...jobDetail,
  //     [key]: value,
  //   });
  // };

  const submitHandler = () => {
    console.log(jobDetail);
  };
  const inputHandler = (key, value) => {
    // e.preventDefault();
    setjobDetail({
      ...jobDetail,
      [key]: value,
    });
    // const data = setSelected(e.target.selected);
    // console.log(data);

    // const newdata = { ...selected };
    // console.log(newdata);
    // newdata[e.target.id] = e.target.value;
    // setSelected(newdata);
    // console.log(newdata);
  };

  return (
    // Paper use as container , elevation - for shadow
    <Paper elevation={3} className={classes.body}>
      <Navbar />
      <Grid container direction="column" spacing={4} alignItems="center">
        <Grid item>
          {/*  Typography use for size or different heading tag */}
          <Typography variant="h4" component="h2">
            Add Jobs
          </Typography>
        </Grid>
        <Grid item>
          <TextField
            id="title"
            type="text"
            label="Title"
            className={classes.inputBox}
            //   onChange={handleChange}
            //   value={data.email}
          />
        </Grid>
        <Grid item>
          <TextField
            id="skill"
            type="text"
            label="Skill"
            className={classes.inputBox}
            //   onChange={handleChange}
            //   value={data.password}
          />
        </Grid>
        {/* <Grid item>
            <TextField
              id="jobTypes"
              type="text"
              label="Job Types"
              className={classes.inputBox}
              onChange={inputHandler}
              value={jobTypes.jobTypes}
            />
          </Grid> */}
        <Grid item>
          <FormControl fullWidth className={classes.inputBox}>
            <InputLabel>Job Type</InputLabel>
            <Select
              id="jobtype"
              value={jobDetail.jobType}
              onChange={(event) => {
                inputHandler('jobType', event.target.value);
              }}
            >
              <MenuItem value="Full Time">Full Time</MenuItem>
              <MenuItem value="Part Time">Part Timne</MenuItem>
            </Select>
          </FormControl>
        </Grid>
        <Grid item>
          <TextField
            id="salary"
            type="text"
            label="Salary"
            className={classes.inputBox}
            //   onChange={handleChange}
            //   value={data.password}
          />
        </Grid>
        <Grid item>
          <TextField
            id="position"
            type="position"
            label="Position"
            className={classes.inputBox}
            //   onChange={handleChange}
            //   value={data.password}
          />
        </Grid>
        <Grid item>
          <Button
            id="btnSubmit"
            variant="contained"
            type="submit"
            className={classes.submitButton}
            onClick={submitHandler}
          >
            Submit
          </Button>
        </Grid>
        {/* <select onChange={inputHandler} value={selected} name="jobtype">
            <option value="Bangalore">Bangalore</option>
            <option value="Delhi">Delhi</option>
            <option value="Mumbai">Mumbai</option>
          </select> */}
      </Grid>
    </Paper>
  );
};

export default AddJob;
