import React from 'react';

import {
  Grid,
  Paper,
  Typography,
  Button,
  TextField,
  makeStyles,
  FormControl,
  Select,
  MenuItem,
  InputLabel,
} from '@material-ui/core';

import { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

import Navbar from '../Navbar';

// makeStyles use for CSS

const useStyles = makeStyles(() => ({
  body: {
    padding: '60px 60px',
    margin: '125px 350px',
  },
  inputBox: {
    width: '300px',
    margin: '-12px',
  },
  submitButton: {
    width: '300px',
    margin: '0px 15px',
    backgroundColor: '#034f84',
    color: 'white',
  },
  LinkColor: {
    textDecoration: 'none',
    color: 'black',
  },
}));

const AddJob = () => {
  const classes = useStyles();

  const [jobTypes, setJobType] = useState({ jobType: '' });

  const handleSubmit = (e) => {
    // e.preventDefault();
    // console.log(jobTypes);
    // console.log(e.target.value);
    // setJobType();
    const url = 'http://localhost:5000/users/register';
    axios
      .post(url, {
        jobType: jobTypes,
        // types: user.types,
      })
      .then((res) => {
        console.log(res.user);
      });
  };

  const inputHandler = (e) => {
    e.preventDefault();
    // console.log(user.types);
    const newdata = { ...jobTypes };
    console.log(newdata);
    newdata[e.target.id] = e.target.value;
    setJobType(newdata);
    console.log(newdata);
  };

  return (
    // Paper use as container , elevation - for shadow

    <form onSubmit={handleSubmit}>
      <Paper elevation={3} className={classes.body}>
        <Navbar />
        <Grid container direction="column" spacing={4} alignItems="center">
          <Grid item>
            {/*  Typography use for size or different heading tag */}

            <Typography variant="h4" component="h2">
              Add Jobs
            </Typography>
          </Grid>
          <Grid item>
            <TextField
              id="title"
              type="text"
              label="Title"
              className={classes.inputBox}
              //   onChange={handleChange}
              //   value={data.email}
            />
          </Grid>
          <Grid item>
            <TextField
              id="skill"
              type="text"
              label="Skill"
              className={classes.inputBox}
              //   onChange={handleChange}
              //   value={data.password}
            />
          </Grid>
          {/* <Grid item>
            <TextField
              id="jobTypes"
              type="text"
              label="Job Types"
              className={classes.inputBox}
              onChange={inputHandler}
              value={jobTypes.jobTypes}
            />
          </Grid> */}
          <Grid item>
            <FormControl fullWidth className={classes.inputBox}>
              <InputLabel>Job Type</InputLabel>
              <Select id="jobTypes" onChange={inputHandler} value={jobTypes}>
                <MenuItem value="fulltime">Full Time</MenuItem>
                <MenuItem value="parttime">Part Timne</MenuItem>
              </Select>
            </FormControl>
          </Grid>
          <Grid item>
            <TextField
              id="salary"
              type="text"
              label="Salary"
              className={classes.inputBox}
              //   onChange={handleChange}
              //   value={data.password}
            />
          </Grid>
          <Grid item>
            <TextField
              id="position"
              type="position"
              label="Position"
              className={classes.inputBox}
              //   onChange={handleChange}
              //   value={data.password}
            />
          </Grid>

          <Grid item>
            <Button
              id="btnSubmit"
              variant="contained"
              type="submit"
              className={classes.submitButton}
            >
              Submit
            </Button>
          </Grid>
        </Grid>
      </Paper>
    </form>
  );
};

export default AddJob;
