        localStorage.setItem('types', res.data.types);
        setLoggedin(isAuth());
        console.log('Logged in successfully');
        console.log(res);
      });
  };

  return (
    // Paper use as container , elevation - for shadow

    <Paper elevation={3} className={classes.body}>
